server/gemini.ts.content:
import { GoogleGenAI } from "@google/genai";
import { type GeneratedQuiz, type QuizQuestion } from "@shared/schema";

function getAIClient() {
  return new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY || "" });
}

export async function generateQuiz(field: string, questionCount: number = 20): Promise<GeneratedQuiz> {
  const fieldDescriptions = {
    "web-development": "modern web development including HTML5, CSS3, JavaScript ES6+, React, Vue, Angular, Node.js, Express, databases, APIs, responsive design, security, performance optimization, and deployment",
    "android-development": "Android app development including Java, Kotlin, Android SDK, Activities, Fragments, Intents, UI/UX design, data persistence, networking, testing, publishing, and performance optimization",
    "reverse-engineering": "software reverse engineering including disassembly, decompilation, static/dynamic analysis, debugging, malware analysis, binary exploitation, code obfuscation, and security research",
    "ethical-hacking": "ethical hacking and penetration testing including network security, web application security, system vulnerabilities, OWASP Top 10, Metasploit, Burp Suite, social engineering, and security frameworks"
  };

  const fieldDescription = fieldDescriptions[field as keyof typeof fieldDescriptions] || field;

  const prompt = `Generate an educational quiz with exactly ${questionCount} multiple choice questions about ${fieldDescription}. 

  Focus on practical, real-world scenarios and current industry standards. Questions should test:
  - Fundamental concepts and terminology
  - Best practices and methodologies  
  - Problem-solving skills
  - Security considerations
  - Performance optimization
  - Industry tools and frameworks

  Each question should:
  - Be challenging but fair for intermediate learners
  - Have exactly 4 options (A, B, C, D)
  - Have only one correct answer
  - Include a detailed explanation for the correct answer
  - Be unique and cover different aspects of the field
  - Use realistic scenarios when possible

  Format the response as JSON with this structure:
  {
    "title": "Quiz title for ${field}",
    "category": "${field}",
    "questions": [
      {
        "id": "q1",
        "question": "Question text with practical context",
        "options": ["Option A", "Option B", "Option C", "Option D"],
        "correctAnswer": 0,
        "explanation": "Detailed explanation of why this answer is correct and why others are wrong"
      }
    ],
    "timeLimit": ${questionCount * 60}
  }

  Make the questions educational, practical, and aligned with current industry standards.`;

  async function attemptQuizGeneration(): Promise<GeneratedQuiz> {
    const ai = getAIClient();

    try {
      const response = await ai.models.generateContent({
        model: "gemini-2.5-flash",
        config: {
          responseMimeType: "application/json",
          responseSchema: {
            type: "object",
            properties: {
              title: { type: "string" },
              category: { type: "string" },
              questions: {
                type: "array",
                items: {
                  type: "object",
                  properties: {
                    id: { type: "string" },
                    question: { type: "string" },
                    options: {
                      type: "array",
                      items: { type: "string" },
                      minItems: 4,
                      maxItems: 4
                    },
                    correctAnswer: { type: "number" },
                    explanation: { type: "string" }
                  },
                  required: ["id", "question", "options", "correctAnswer", "explanation"]
                }
              },
              timeLimit: { type: "number" }
            },
            required: ["title", "category", "questions", "timeLimit"]
          }
        },
        contents: prompt,
      });

      const rawJson = response.text;
      if (!rawJson) {
        throw new Error("Empty response from Gemini API");
      }

      const quizData: GeneratedQuiz = JSON.parse(rawJson);

      // Validate the response
      if (!quizData.questions || quizData.questions.length !== questionCount) {
        throw new Error("Invalid quiz format received from AI");
      }

      // Add unique IDs if not provided
      quizData.questions = quizData.questions.map((q, index) => ({
        ...q,
        id: q.id || `q_${index + 1}`,
      }));

      return quizData;
    } catch (error: any) {
      throw error;
    }
  }

  try {
    return await attemptQuizGeneration();
  } catch (error) {
    console.error("Error generating quiz with Gemini:", error);
    throw new Error(`Failed to generate quiz: ${error instanceof Error ? error.message : "Unknown error"}`);
  }
}

export function calculateScore(questions: QuizQuestion[], userAnswers: number[]): { score: number; pointsEarned: number } {
  if (questions.length !== userAnswers.length) {
    throw new Error("Questions and answers length mismatch");
  }

  const correctAnswers = userAnswers.filter((answer, index) => 
    answer === questions[index].correctAnswer
  ).length;

  const score = Math.round((correctAnswers / questions.length) * 100);
  const pointsEarned = correctAnswers * 10 + (score >= 80 ? 50 : score >= 60 ? 20 : 0); // Bonus points for high scores

  return { score, pointsEarned };
}

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { setupAuth } from "./auth";
import { storage } from "./storage";
import { generateQuiz, calculateScore } from "./gemini";
import { insertQuizSchema, insertQuizAttemptSchema, quizSubmissionSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup authentication routes
  setupAuth(app);

  // Quiz generation endpoint
  app.post("/api/quiz/generate", async (req, res, next) => {
    try {
      if (!req.isAuthenticated()) {
        return res.status(401).json({ error: "Authentication required" });
      }

      const { field, questionCount = 20, isDailyQuiz = false } = req.body;
      if (!field) {
        return res.status(400).json({ error: "Field is required" });
      }

      const userIp = req.ip || req.connection.remoteAddress || "";

      // Check if user already took daily quiz today for this field
      if (isDailyQuiz) {
        const canTakeDaily = await storage.canTakeDailyQuiz(req.user!.id, field, userIp);
        if (!canTakeDaily) {
          return res.status(400).json({ error: "Daily quiz already completed for this field today" });
        }
      }

      const generatedQuiz = await generateQuiz(field, questionCount);

      // Save quiz to database
      const quiz = await storage.createQuiz({
        title: generatedQuiz.title,
        category: generatedQuiz.category,
        field: field,
        questions: generatedQuiz.questions,
        timeLimit: generatedQuiz.timeLimit,
        isDailyQuiz,
      });

      res.json({
        id: quiz.id,
        title: quiz.title,
        category: quiz.category,
        field: quiz.field,
        questions: quiz.questions,
        timeLimit: quiz.timeLimit,
        isDailyQuiz,
      });
    } catch (error) {
      next(error);
    }
  });

  // Submit quiz attempt
  app.post("/api/quiz/submit", async (req, res, next) => {
    try {
      if (!req.isAuthenticated()) {
        return res.status(401).json({ error: "Authentication required" });
      }

      const submissionData = quizSubmissionSchema.parse(req.body);

      // Get quiz to validate answers
      const quiz = await storage.getQuizById(submissionData.quizId);
      if (!quiz) {
        return res.status(404).json({ error: "Quiz not found" });
      }

      // Calculate score
      const { score, pointsEarned } = calculateScore(
        quiz.questions as any,
        submissionData.answers as number[]
      );

      const userIp = req.ip || req.connection.remoteAddress || "";

      // Create quiz attempt
      const attempt = await storage.createQuizAttempt({
        userId: req.user!.id,
        quizId: submissionData.quizId,
        answers: submissionData.answers,
        timeSpent: submissionData.timeSpent,
        score,
        pointsEarned,
        field: quiz.field || quiz.category,
        isDailyQuiz: quiz.isDailyQuiz || false,
        ipAddress: userIp,
      });

      // Update user stats for all quiz attempts
      await storage.updateUserStats(req.user!.id, pointsEarned, quiz.field || quiz.category);

      res.json({
        id: attempt.id,
        score,
        pointsEarned,
        correctAnswers: quiz.questions as any,
        userAnswers: submissionData.answers,
        timeSpent: submissionData.timeSpent,
        quizTitle: quiz.title,
        totalQuestions: quiz.questions.length,
      });
    } catch (error) {
      next(error);
    }
  });

  // Get quiz by ID
  app.get("/api/quiz/:id", async (req, res, next) => {
    try {
      if (!req.isAuthenticated()) {
        return res.status(401).json({ error: "Authentication required" });
      }

      const quiz = await storage.getQuizById(req.params.id);
      if (!quiz) {
        return res.status(404).json({ error: "Quiz not found" });
      }

      res.json(quiz);
    } catch (error) {
      next(error);
    }
  });

  // Get leaderboard
  app.get("/api/leaderboard", async (req, res, next) => {
    try {
      if (!req.isAuthenticated()) {
        return res.status(401).json({ error: "Authentication required" });
      }

      const limit = parseInt(req.query.limit as string) || 50;
      const leaderboard = await storage.getLeaderboard(limit);

      res.json(leaderboard);
    } catch (error) {
      next(error);
    }
  });

  // Get user profile with stats
  app.get("/api/profile", async (req, res, next) => {
    try {
      if (!req.isAuthenticated()) {
        return res.status(401).json({ error: "Authentication required" });
      }

      const userWithStats = await storage.getUserWithStats(req.user!.id);
      if (!userWithStats) {
        return res.status(404).json({ error: "User not found" });
      }

      res.json(userWithStats);
    } catch (error) {
      next(error);
    }
  });

  // Get user quiz history
  app.get("/api/quiz-history", async (req, res, next) => {
    try {
      if (!req.isAuthenticated()) {
        return res.status(401).json({ error: "Authentication required" });
      }

      const attempts = await storage.getUserQuizAttempts(req.user!.id);
      res.json(attempts);
    } catch (error) {
      next(error);
    }
  });

  // Get daily quiz status
  app.get("/api/daily-quiz-status", async (req, res, next) => {
    try {
      if (!req.isAuthenticated()) {
        return res.status(401).json({ error: "Authentication required" });
      }

      const userIp = req.ip || req.connection.remoteAddress || "";
      const status = await storage.getDailyQuizStatus(req.user!.id, userIp);
      res.json(status);
    } catch (error) {
      next(error);
    }
  });

  // Get field-based leaderboard
  app.get("/api/leaderboard/:field", async (req, res, next) => {
    try {
      if (!req.isAuthenticated()) {
        return res.status(401).json({ error: "Authentication required" });
      }

      const limit = parseInt(req.query.limit as string) || 50;
      const leaderboard = await storage.getFieldLeaderboard(req.params.field, limit);
      res.json(leaderboard);
    } catch (error) {
      next(error);
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { users, quizzes, quizAttempts, type User, type InsertUser, type Quiz, type InsertQuiz, type QuizAttempt, type InsertQuizAttempt, type UserWithStats, type LeaderboardEntry } from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql } from "drizzle-orm";
import session from "express-session";
import connectPg from "connect-pg-simple";
import { pool } from "./db";

const PostgresSessionStore = connectPg(session);

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  createQuiz(quiz: InsertQuiz): Promise<Quiz>;
  getQuizzesByCategory(category: string): Promise<Quiz[]>;
  getQuizById(id: string): Promise<Quiz | undefined>;

  createQuizAttempt(attempt: InsertQuizAttempt): Promise<QuizAttempt>;
  getUserQuizAttempts(userId: string): Promise<QuizAttempt[]>;
  updateUserStats(userId: string, pointsEarned: number, field: string): Promise<void>;

  getLeaderboard(limit?: number): Promise<LeaderboardEntry[]>;
  canTakeDailyQuiz(userId: string, field: string, ipAddress: string): Promise<boolean>;
  getDailyQuizStatus(userId: string, ipAddress: string): Promise<Record<string, boolean>>;
  getFieldLeaderboard(field: string, limit?: number): Promise<LeaderboardEntry[]>;
  getUserWithStats(userId: string): Promise<UserWithStats | undefined>;

  sessionStore: session.SessionStore;
}

export class DatabaseStorage implements IStorage {
  sessionStore: session.SessionStore;

  constructor() {
    this.sessionStore = new PostgresSessionStore({ 
      pool, 
      createTableIfMissing: true 
    });
  }

  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async createQuiz(insertQuiz: InsertQuiz): Promise<Quiz> {
    const [quiz] = await db
      .insert(quizzes)
      .values(insertQuiz)
      .returning();
    return quiz;
  }

  async getQuizzesByCategory(category: string): Promise<Quiz[]> {
    return await db
      .select()
      .from(quizzes)
      .where(eq(quizzes.category, category))
      .orderBy(desc(quizzes.createdAt))
      .limit(10);
  }

  async getQuizById(id: string): Promise<Quiz | undefined> {
    const [quiz] = await db.select().from(quizzes).where(eq(quizzes.id, id));
    return quiz || undefined;
  }

  async createQuizAttempt(insertAttempt: InsertQuizAttempt): Promise<QuizAttempt> {
    const [attempt] = await db
      .insert(quizAttempts)
      .values(insertAttempt)
      .returning();
    return attempt;
  }

  async getUserQuizAttempts(userId: string): Promise<QuizAttempt[]> {
    return await db
      .select()
      .from(quizAttempts)
      .where(eq(quizAttempts.userId, userId))
      .orderBy(desc(quizAttempts.completedAt))
      .limit(20);
  }

  async getLeaderboard(limit: number = 50): Promise<LeaderboardEntry[]> {
    const result = await db
      .select({
        id: users.id,
        username: users.username,
        totalScore: users.totalScore,
        quizzesTaken: users.quizzesTaken,
      })
      .from(users)
      .where(sql`${users.quizzesTaken} > 0`)
      .orderBy(desc(users.totalScore))
      .limit(limit);

    return result.map((user, index) => ({
      ...user,
      averageScore: user.quizzesTaken > 0 ? Math.round((user.totalScore / user.quizzesTaken) * 100 / 100) : 0,
      rank: index + 1,
    }));
  }

  async getUserWithStats(userId: string): Promise<UserWithStats | undefined> {
    const user = await this.getUser(userId);
    if (!user) return undefined;

    const leaderboard = await this.getLeaderboard();
    const userRank = leaderboard.find(entry => entry.id === userId);

    return {
      ...user,
      rank: userRank?.rank,
      averageScore: user.quizzesTaken > 0 ? Math.round((user.totalScore / user.quizzesTaken) * 100 / 100) : 0,
    };
  }

  async canTakeDailyQuiz(userId: string, field: string, ipAddress: string): Promise<boolean> {
    const today = new Date().toISOString().split('T')[0];

    // Check by user ID and IP
    const existingAttempt = await db
      .select()
      .from(quizAttempts)
      .where(
        sql`${quizAttempts.userId} = ${userId} 
        AND ${quizAttempts.field} = ${field}
        AND ${quizAttempts.isDailyQuiz} = true
        AND DATE(${quizAttempts.completedAt}) = ${today}`
      )
      .limit(1);

    const ipAttempt = await db
      .select()
      .from(quizAttempts)
      .where(
        sql`${quizAttempts.ipAddress} = ${ipAddress}
        AND ${quizAttempts.field} = ${field}
        AND ${quizAttempts.isDailyQuiz} = true
        AND DATE(${quizAttempts.completedAt}) = ${today}`
      )
      .limit(1);

    return existingAttempt.length === 0 && ipAttempt.length === 0;
  }

  async getDailyQuizStatus(userId: string, ipAddress: string): Promise<Record<string, boolean>> {
    const today = new Date().toISOString().split('T')[0];
    const fields = ["web-development", "android-development", "reverse-engineering", "ethical-hacking"];

    const status: Record<string, boolean> = {};

    for (const field of fields) {
      const canTake = await this.canTakeDailyQuiz(userId, field, ipAddress);
      status[field] = !canTake; // true if already taken
    }

    return status;
  }

  async updateUserStats(userId: string, pointsEarned: number, field: string): Promise<void> {
    await db
      .update(users)
      .set({
        totalScore: sql`${users.totalScore} + ${pointsEarned}`,
        quizzesTaken: sql`${users.quizzesTaken} + 1`,
      })
      .where(eq(users.id, userId));
  }

  async getFieldLeaderboard(field: string, limit: number = 50): Promise<LeaderboardEntry[]> {
    const result = await db
      .select({
        id: users.id,
        username: users.username,
        totalScore: sql<number>`COALESCE(SUM(${quizAttempts.pointsEarned}), 0)`.as('fieldScore'),
        quizzesTaken: sql<number>`COUNT(${quizAttempts.id})`.as('fieldQuizzes'),
      })
      .from(users)
      .leftJoin(quizAttempts, 
        sql`${users.id} = ${quizAttempts.userId} 
        AND ${quizAttempts.field} = ${field} 
        AND ${quizAttempts.isDailyQuiz} = true`
      )
      .groupBy(users.id, users.username)
      .having(sql`COUNT(${quizAttempts.id}) > 0`)
      .orderBy(sql`fieldScore DESC`)
      .limit(limit);

    return result.map((user, index) => ({
      ...user,
      totalScore: user.totalScore,
      quizzesTaken: user.quizzesTaken,
      averageScore: user.quizzesTaken > 0 ? Math.round((user.totalScore / user.quizzesTaken)) : 0,
      rank: index + 1,
    }));
  }
}

export const storage = new DatabaseStorage();

