.neon-glow {
  position: relative;
  overflow: hidden;
}

.neon-glow::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, 
    transparent, 
    hsl(var(--primary) / 0.4), 
