  transition: left 0.8s;
}

.neon-glow:hover::before {
  left: 100%;
}

/* Enhanced button styles */
.btn-primary {
  background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--accent)) 100%);
  border: none;
  color: white;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  transition: all 0.3s cubic-bezier(0.16, 1, 0.3, 1);
  position: relative;
  overflow: hidden;
}

.btn-primary:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 30px -5px hsl(var(--primary) / 0.4);
}

.btn-primary::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, 
    transparent, 
    hsl(var(--background) / 0.2), 
    transparent);
  transition: left 0.5s;
}

.btn-primary:hover::before {
  left: 100%;
}

/* Progress bars with enhanced animations */
.progress-animated {
  background: linear-gradient(135deg, hsl(var(--muted)) 0%, hsl(var(--card)) 100%);
  overflow: hidden;
  position: relative;
}

.progress-animated::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  background: linear-gradient(90deg, 
    hsl(var(--primary)) 0%, 
    hsl(var(--accent)) 50%, 
    hsl(var(--primary)) 100%);
  transition: width 1s cubic-bezier(0.16, 1, 0.3, 1);
  box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
}

/* Achievement badges */
.achievement-badge {
  background: linear-gradient(135deg, 
    hsl(var(--primary) / 0.1) 0%, 
    hsl(var(--accent) / 0.1) 100%);
  border: 2px solid hsl(var(--primary) / 0.2);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.achievement-badge:hover {
  border-color: hsl(var(--primary) / 0.5);
  transform: scale(1.05);
}

.achievement-badge::after {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: conic-gradient(
    transparent, 
    hsl(var(--primary) / 0.1), 
    transparent 30%);
