import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useAuth } from "@/hooks/use-auth";
import { Trophy, Medal, Award } from "lucide-react";
import type { LeaderboardEntry } from "@shared/schema";

export default function Leaderboard() {
  const { user } = useAuth();

  const { data: leaderboard, isLoading } = useQuery<LeaderboardEntry[]>({
    queryKey: ["/api/leaderboard"],
  });

  if (isLoading) {
    return (
      <div className="space-y-6 animate-pulse">
        <div className="h-8 bg-muted rounded w-1/3"></div>
        <div className="h-32 bg-muted rounded-lg"></div>
        <div className="h-96 bg-muted rounded-lg"></div>
      </div>
    );
  }

  const topThree = leaderboard?.slice(0, 3) || [];
  const restOfLeaderboard = leaderboard?.slice(3) || [];
  const userEntry = leaderboard?.find(entry => entry.username === user?.username);

  return (
    <div className="space-y-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-foreground mb-2">Leaderboard</h1>
        <p className="text-muted-foreground">See how you stack up against other quiz masters</p>
      </div>

      {/* Top 3 Podium */}
      {topThree.length >= 3 && (
        <Card className="shadow-sm">
          <CardContent className="p-8">
            <div className="flex justify-center items-end space-x-8">
              {/* 2nd Place */}
              <div className="text-center" data-testid="podium-second">
                <div className="bg-muted w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-2">
                  <span className="text-xl font-bold text-foreground">
                    {topThree[1]?.username.slice(0, 2).toUpperCase()}
                  </span>
                </div>
                <h3 className="font-semibold text-foreground">{topThree[1]?.username}</h3>
                <p className="text-sm text-muted-foreground">{topThree[1]?.totalScore.toLocaleString()} pts</p>
                <div className="bg-chart-2 w-20 h-16 rounded-t-lg mt-2 flex items-center justify-center">
                  <span className="text-white font-bold text-lg">2</span>
                </div>
              </div>

              {/* 1st Place */}
              <div className="text-center" data-testid="podium-first">
                <div className="bg-accent w-20 h-20 rounded-full flex items-center justify-center mx-auto mb-2 border-4 border-accent/30">
                  <span className="text-xl font-bold text-accent-foreground">
                    {topThree[0]?.username.slice(0, 2).toUpperCase()}
                  </span>
                </div>
                <h3 className="font-semibold text-foreground">{topThree[0]?.username}</h3>
                <p className="text-sm text-muted-foreground">{topThree[0]?.totalScore.toLocaleString()} pts</p>
                <div className="bg-accent w-20 h-24 rounded-t-lg mt-2 flex items-center justify-center">
                  <span className="text-white font-bold text-xl">1</span>
                </div>
              </div>

              {/* 3rd Place */}
              <div className="text-center" data-testid="podium-third">
                <div className="bg-muted w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-2">
                  <span className="text-xl font-bold text-foreground">
                    {topThree[2]?.username.slice(0, 2).toUpperCase()}
                  </span>
                </div>
                <h3 className="font-semibold text-foreground">{topThree[2]?.username}</h3>
                <p className="text-sm text-muted-foreground">{topThree[2]?.totalScore.toLocaleString()} pts</p>
                <div className="bg-chart-3 w-20 h-12 rounded-t-lg mt-2 flex items-center justify-center">
                  <span className="text-white font-bold text-lg">3</span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Full Leaderboard */}
      <Card className="shadow-sm">
        <CardHeader>
          <CardTitle>Full Rankings</CardTitle>
        </CardHeader>
        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-muted">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                    Rank
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                    Player
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                    Score
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                    Quizzes
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase tracking-wider">
                    Avg %
                  </th>
                </tr>
              </thead>
              <tbody className="bg-card divide-y divide-border">
                {leaderboard?.map((player) => {
                  const isCurrentUser = player.username === user?.username;
                  return (
                    <tr
                      key={player.id}
                      className={`hover:bg-muted transition-colors ${
                        isCurrentUser ? "bg-primary/5" : ""
                      }`}
                      data-testid={`leaderboard-row-${player.rank}`}
                    >
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center space-x-2">
                          <span className="text-lg font-bold leaderboard-rank">
                            {player.rank}
                          </span>
                          {player.rank <= 3 && (
                            <div className="text-accent">
                              {player.rank === 1 && <Trophy className="h-4 w-4" />}
                              {player.rank === 2 && <Medal className="h-4 w-4" />}
                              {player.rank === 3 && <Award className="h-4 w-4" />}
                            </div>
                          )}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div className="bg-primary/10 w-8 h-8 rounded-full flex items-center justify-center mr-3">
                            <span className="text-sm font-medium text-primary">
                              {player.username.slice(0, 2).toUpperCase()}
                            </span>
                          </div>
                          <span className="font-medium text-foreground">
                            {player.username}
                            {isCurrentUser && (
                              <span className="text-xs bg-primary text-primary-foreground px-2 py-1 rounded-full ml-2">
                                You
                              </span>
                            )}
                          </span>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-foreground">
                        {player.totalScore.toLocaleString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-muted-foreground">
                        {player.quizzesTaken}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-muted-foreground">
                        {player.averageScore}%
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>

      {/* User Position Highlight */}
      {userEntry && userEntry.rank > 10 && (
        <Card className="shadow-sm border-primary/20">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <span className="text-lg font-bold text-primary">#{userEntry.rank}</span>
                <span className="font-medium text-foreground">Your Position</span>
              </div>
              <span className="text-sm text-muted-foreground">
                {userEntry.totalScore.toLocaleString()} points
              </span>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/profile/profile.ts.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Separator } from "@/components/ui/separator";
import { Switch } from "@/components/ui/switch";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useQuery } from "@tanstack/react-query";
import { 
  Trophy, Star, Target, TrendingUp, Clock, Award, Settings, Palette, 
  Volume2, Bell, Shield, User, Moon, Sun, Monitor, Sparkles, Zap,
  Crown, Medal, ChevronRight, Activity, Calendar, BarChart3,
  Eye, EyeOff, Save, RefreshCw, Gamepad2, Globe, Lock
} from "lucide-react";
import { useState, useEffect } from "react";

export default function Profile() {
  const [showSettings, setShowSettings] = useState(false);
  const [theme, setTheme] = useState("system");
  const [soundEnabled, setSoundEnabled] = useState(true);
  const [notificationsEnabled, setNotificationsEnabled] = useState(true);
  const [animationsEnabled, setAnimationsEnabled] = useState(true);
  const [privacyMode, setPrivacyMode] = useState(false);
  const [language, setLanguage] = useState("en");
  const [autoSave, setAutoSave] = useState(true);
  const [particlesEnabled, setParticlesEnabled] = useState(true);

  const { data: profile } = useQuery({
    queryKey: ["/api/profile"],
  });

  const { data: quizHistory } = useQuery({
    queryKey: ["/api/quiz-history"],
  });

  // Apply theme changes
  useEffect(() => {
    const root = document.documentElement;
    if (theme === "dark") {
      root.classList.add("dark");
    } else if (theme === "light") {
      root.classList.remove("dark");
    } else {
      // System theme
      const mediaQuery = window.matchMedia("(prefers-color-scheme: dark)");
      if (mediaQuery.matches) {
        root.classList.add("dark");
      } else {
        root.classList.remove("dark");
      }
    }
  }, [theme]);

  const applySettings = () => {
    // Save settings to localStorage
    const settings = {
      theme,
      soundEnabled,
      notificationsEnabled,
      animationsEnabled,
      privacyMode,
      language,
      autoSave,
      particlesEnabled
    };
    localStorage.setItem('userSettings', JSON.stringify(settings));
    setShowSettings(false);

    // Show success notification
    if (window.toast) {
      window.toast({
        title: "Settings Applied! âœ¨",
        description: "Your preferences have been saved successfully.",
      });
    }
  };

  if (!profile) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-background/95 to-primary/5 relative overflow-hidden">
        {/* Animated background particles */}
        <div className="absolute inset-0 overflow-hidden">
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="absolute animate-float opacity-20"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
                animationDelay: `${Math.random() * 4}s`,
                animationDuration: `${4 + Math.random() * 4}s`
              }}
            >
              <Sparkles className="h-4 w-4 text-primary" />
            </div>
          ))}
        </div>

        <div className="relative z-10 max-w-7xl mx-auto p-6 space-y-8">
          {/* Loading skeleton with glassmorphism */}
          <div className="glass-morphism p-8 rounded-3xl border border-primary/20 backdrop-blur-xl">
            <div className="flex items-center space-x-6">
              <div className="w-24 h-24 bg-gradient-to-br from-primary/30 to-accent/30 rounded-full skeleton"></div>
              <div className="space-y-3">
                <div className="h-8 w-48 skeleton rounded-full"></div>
                <div className="h-4 w-32 skeleton rounded-full"></div>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(6)].map((_, i) => (
              <div key={i} className="glass-morphism p-6 rounded-2xl border border-primary/10 skeleton h-40"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  const stats = [
    {
      icon: Trophy,
      label: "Total Score",
      value: profile.totalScore?.toLocaleString() || "0",
      color: "text-yellow-400",
      bgGradient: "from-yellow-500/20 to-amber-500/20",
      glowColor: "shadow-yellow-500/20",
      particles: true
    },
    {
      icon: Star,
      label: "Average Score",
      value: profile.averageScore ? `${profile.averageScore.toFixed(1)}%` : "0%",
      color: "text-blue-400",
      bgGradient: "from-blue-500/20 to-cyan-500/20",
      glowColor: "shadow-blue-500/20",
      particles: true
    },
    {
      icon: Target,
      label: "Quizzes Taken",
      value: profile.totalQuizzes?.toString() || "0",
      color: "text-emerald-400",
      bgGradient: "from-emerald-500/20 to-green-500/20",
      glowColor: "shadow-emerald-500/20",
      particles: false
    },
    {
      icon: TrendingUp,
      label: "Best Score",
      value: profile.bestScore ? `${profile.bestScore}%` : "0%",
      color: "text-purple-400",
      bgGradient: "from-purple-500/20 to-pink-500/20",
      glowColor: "shadow-purple-500/20",
      particles: true
    },
    {
      icon: Clock,
      label: "Time Spent",
      value: profile.totalTimeSpent ? `${Math.round(profile.totalTimeSpent / 60)}h` : "0h",
      color: "text-orange-400",
      bgGradient: "from-orange-500/20 to-red-500/20",
      glowColor: "shadow-orange-500/20",
      particles: false
    },
    {
      icon: Award,
      label: "Achievements",
      value: profile.achievements?.length?.toString() || "0",
      color: "text-rose-400",
      bgGradient: "from-rose-500/20 to-pink-500/20",
      glowColor: "shadow-rose-500/20",
      particles: true
    },
  ];

  const recentQuizzes = quizHistory?.slice(0, 5) || [];

  const getScoreColor = (score: number) => {
    if (score >= 90) return "text-emerald-400 bg-gradient-to-r from-emerald-500/20 to-green-500/20 border-emerald-500/30";
    if (score >= 80) return "text-blue-400 bg-gradient-to-r from-blue-500/20 to-cyan-500/20 border-blue-500/30";
    if (score >= 70) return "text-yellow-400 bg-gradient-to-r from-yellow-500/20 to-amber-500/20 border-yellow-500/30";
    if (score >= 60) return "text-orange-400 bg-gradient-to-r from-orange-500/20 to-red-500/20 border-orange-500/30";
    return "text-red-400 bg-gradient-to-r from-red-500/20 to-pink-500/20 border-red-500/30";
  };

  const getRankIcon = (score: number) => {
    if (score >= 95) return { icon: Crown, color: "text-yellow-400", gradient: "from-yellow-400 to-amber-400", glow: "shadow-yellow-400/50" };
    if (score >= 90) return { icon: Trophy, color: "text-yellow-400", gradient: "from-yellow-500 to-amber-500", glow: "shadow-yellow-500/40" };
    if (score >= 80) return { icon: Medal, color: "text-gray-300", gradient: "from-gray-300 to-gray-400", glow: "shadow-gray-300/40" };
    if (score >= 70) return { icon: Award, color: "text-amber-600", gradient: "from-amber-600 to-yellow-600", glow: "shadow-amber-600/40" };
    return { icon: Target, color: "text-blue-400", gradient: "from-blue-400 to-cyan-400", glow: "shadow-blue-400/40" };
  };

